import './CPAdmin.css';
import { useState,useEffect } from 'react';
import axios from 'axios';
import { _userapiurl } from '../../api_url';

function CPAdmin(){
    const [output, setOutput] = useState();
    const [opassword, setOPassword] = useState();
    const [npassword, setNPassword] = useState();
    const [cnpassword, setCNPassword] = useState();
    const handleSubmit=()=>{
        const userDetails={"email":localStorage.getItem("email"),"password":opassword};
           axios.get(_userapiurl+"login",userDetails).then((res)=>{
                  // console.log(res.data);
                   var token= res.data.token;
                   var userDetails=res.data.userDetails;
                   localStorage.setItem("token",token);
                   localStorage.setItem("_id",userDetails._id);
                   localStorage.setItem("name",userDetails.name);
                   localStorage.setItem("email",userDetails.email);
                   localStorage.setItem("mobile",userDetails.mobile);
                   localStorage.setItem("city",userDetails.city);
                   localStorage.setItem("gender",userDetails.gender);
                   localStorage.setItem("address",userDetails.address);
                   localStorage.setItem("status",userDetails.status);
                   localStorage.setItem("info",userDetails.info);
                   localStorage.setItem("role",userDetails.role);
                   (userDetails.role=="admin")?navigate("/admin"):navigate("/user");
           }).catch((err)=>{
                setOutput("Invalid User/Verify your Account");
                setEmail("");
                setPassword("");
           });
   }
    return(


        <>
         {/* About Start */}
    <div class="container py-5">
        <div class="row py-">
            <div class="col-lg-14 pb-5 pb-lg-0 px-3 px-lg-5">
                {/* <h4 class="text-secondary mb-3">Change Password!!!</h4> */}
                <h4 class="text-secondary mb-3"></h4>
                <h1 class="display-4 mb-4"><span class="text-primary">Change</span> <span class="text-secondary">Password!!!</span></h1>
                <font color="orange">{output}</font>
                <form>
  <div class="form-group">
    <label for="opassword">Old Password:</label>
    <input type="password" class="form-control" value={opassword} onChange={e=>setOPassword(e.target.value)}/>
  </div>
  <br/>
  <div class="form-group">
    <label for="npassword"> New Password:</label>
    <input type="password" class="form-control" value={npassword} onChange={e=>setNPassword(e.target.value)}/>
  </div>
  <br/>
  <div class="form-group">
    <label for="cnpassword">Confirm Password:</label>
    <input type="password" class="form-control" value={cnpassword} onChange={e=>setCNPassword(e.target.value)}/>
  </div>
  <br/>
  <button type="button" class="btn btn-success" onClick={handleSubmit}>Submit</button>
</form>
            </div>
        </div>
    </div>
    {/* About End */}
        </>
    );
}
export default CPAdmin;
