import './About.css';
import { useState } from 'react';
function About(){
  const [a , setA] = useState(10);
  const [b, setB] = useState(20);
  const [cDate,  setCurrentDate] = useState(Date());
  const [count, setCount]=useState(0);
  const [users, userDetails]=useState({"uid":1001,"uname":"saket@gmail.com","password":"123456"});

  const incrementCounter=()=>{
    setCount(count+1);

  }
  const decrementCounter=()=>{
    setCount(count-1);

  }
  const resetCounter=()=>{
    setCount(useState)
  }
  const [mks, setMks]=useState([10,20,56,58,98]);
  setTimeout(()=>{
    setCurrentDate(Date());
  },1000);
    return(
        <>
        <div id="content">
          <h2>Welcome To About page</h2>
          <p>A ={a} </p>
          <p>B={b} </p>
          <p>ADD ={a+b} </p>
          <p>Date/Time {cDate}</p>
          &nbsp; &nbsp;
          <h2>Counter : {count}</h2>
          <button onClick={incrementCounter}>IncrementCounter</button>
          <button onClick={decrementCounter}>DecrementCounter</button>
          <button onClick={resetCounter}>Reset Counter</button>
        <h2>Manual Access of Marks</h2>
          <p>Mark 1: {mks[0]}</p>
          <p>Mark 2: {mks[1]}</p>
          <p>Mark 3: {mks[2]}</p>
          <p>Mark 4: {mks[3]}</p>
          <p>Mark 5: {mks[4]}</p>
          <br/><br/>
          <h2>Loop Access</h2>
          {
            mks.map((x)=>(
              <p> {x} </p>

            ))
          }
          <h3> Users Manual Access</h3>
          <p>UserId: {users.uid}</p>
          <p>UserId: {users.uname}</p>
          <p>UserId: {users.password}</p>
          <h5>Users Loop Access</h5>
          {
          Object.keys(users).map((k)=>(
              <p>{k}----{users[k]} </p>
          ))
          }

      </div>
        </>
    );
}
export default About;
